import React, { useState } from 'react';

const CustomMailchimpForm = () => {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Replace this URL with your actual Mailchimp subscription URL
        const mailchimpUrl = 'https://app.us14.list-manage.com/subscribe/post?u=3fbc60d10876b4d0411515d2c&amp;id=bf037421db&amp;f_id=00c4c2e1f0';

        try {
            const response = await fetch(mailchimpUrl, {
                method: 'POST',
                mode: 'no-cors', // Mailchimp API does not support CORS, so we use 'no-cors'
                body: new URLSearchParams({ 'EMAIL': email }),
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                },
            });

            if (response.ok) {
                setMessage('Subscribed successfully');
            } else {
                setMessage('Subscription failed');
            }
        } catch (error) {
            console.error('Error during subscription', error);
            setMessage('Error during subscription');
        }

        setEmail('');
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="email" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
                placeholder="Enter your email" 
                required 
            />
            <button type="submit">Subscribe</button>
            {message && <div>{message}</div>}
        </form>
    );
};

export default CustomMailchimpForm;
