import React, { useState } from 'react';

const CustomMailchimpForm = ({ inputStyle, buttonStyle, placeholder }) => {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        const mailchimpUrl = 'https://app.us14.list-manage.com/subscribe/post?u=3fbc60d10876b4d0411515d2c&amp;id=bf037421db&amp;f_id=00c4c2e1f0';

        try {
            const response = await fetch(mailchimpUrl, {
                method: 'POST',
                mode: 'no-cors', 
                body: new URLSearchParams({ 'EMAIL': email }),
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                },
            });
            if (response) {
                setMessage('Subscribed successfully');
            } 
        } catch (error) {
            console.error('Error during subscription', error);
            setMessage('Error during subscription');
        }

        setEmail('');
    };

    return (
        <form onSubmit={handleSubmit} className="flex flex-wrap">
            <input 
                type="email" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
                placeholder={placeholder}
                className={inputStyle}
                required 
            />
            <button type="submit" className={buttonStyle}>
                Subscribe
            </button>
            {message && <div className="mt-2">{message}</div>}
        </form>
    );
};

export default CustomMailchimpForm;
